import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000',
  headers: {
    'Content-Type': 'application/json',
  }
});

export const generateUSPs = async (url) => {
  try {
    const response = await api.post('/api/generate', { url });
    return response.data;
  } catch (error) {
    throw new Error(error.response?.data?.error || 'Failed to generate USPs');
  }
};

export const generateScript = async (url, usps) => {
  try {
    const response = await api.post('/api/generate-script', { url, usps });
    return response.data;
  } catch (error) {
    throw new Error(error.response?.data?.error || 'Failed to generate script');
  }
};
